//フェードインさせる要素を取得
const FADEIN_ELEM1 = document.getElementById('fadein');
//スクロール時に要素をフェードインさせるイベントを設定する
window.addEventListener('scroll', () => {
    //フェードインする要素の上部の位置を取得する
    const FADEIN_ELEM1_TOP = FADEIN_ELEM1.getBoundingClientRect().top;
    //ブラウザの高さを取得
    const WINDOW_HEIGHT = window.innerHeight;
    //画面に表示された時にフェードインさせる処理
    if (WINDOW_HEIGHT > (FADEIN_ELEM1_TOP + 50)) {
        FADEIN_ELEM1.classList.add('fadein-after');
    }else{
        FADEIN_ELEM1.classList.remove('fadein-after');
    }
});

